package Practice;

public class Maths {

	public static void main(String1[] args) {
		// TODO Auto-generated method stub
		double i =3.14;
		double j = -10;
		System.out.println(i);
		System.out.println(j);
		double z = Math.max(i,j);
		System.out.println("The Max value is : "+z);
		double x = Math.min(i,j);
		System.out.println("The Min value is : "+x);
		double y = Math.abs(j);
		System.out.println("The Absolute value is : "+y);
		double a = Math.sqrt(j);
		System.out.println("The Square root value is : "+a);
		double b = Math.round(i);
		System.out.println("The Round_of DOWN value is : "+b);
		double c = Math.ceil(i);
		System.out.println("The Round_of UP value is : "+c);
		double d = Math.floor(i);
		System.out.println("The Round_of DOWN value is : "+d);		
	}

	
}
